<!DOCTYPE html>
<html>
<head>
	<title>Mostrar Imágenes</title>
</head>
<body>
<h1>Mostrar Imágenes</h1>
	<form method="POST">
		<label>Número de imágenes: <input type="number" name="numImages"></label>
		<button type="submit">Mostrar</button>
	</form>
	<?php
	// Definimos una clase  para generar las imágenes
	class ImageGenerar {
		private $numImages;
		private $imageURL;

		public function __construct($numImages, $imageURL) {
			$this->numImages = $numImages;
			$this->imageURL = $imageURL;
		}

		public function generar() {
			// Creamos una tabla HTML para mostrar las imágenes
			echo "<table>";

			// Creamos una fila para cada imagen
			for ($i = 0; $i < $this->numImages; $i++) {
				echo "<tr>";
				echo "<td><img src='" . $this->imageURL . "'></td>";
				echo "</tr>";
			}

			// Cerramos la tabla
			echo "</table>";
		}
	}

	// Si se ha enviado un formulario con el número de imágenes
	if (isset($_POST['numImages'])) {
		// Convertimos el valor a entero y creamos un objeto ImageGenerator
		$numImages = (int)$_POST['numImages'];
		$imageGenerator = new ImageGenerar($numImages, "data:image/png;base64,");

		// Generamos las imágenes
		$imageGenerator->generar();
	}
	?>

</body>
</html>
